jQuery.fn.vibrate=function(e){var r=jQuery.extend({speed:30,duration:2e3,frequency:5e3,spread:3},e);return this.each(function(){var e=jQuery(this),t=function(){var t=Math.floor(Math.random()*r.spread)-(r.spread-1)/2,a=Math.floor(Math.random()*r.spread)-(r.spread-1)/2,n=Math.floor(Math.random()*r.spread)-(r.spread-1)/2;e.css({left:a+"px",top:t+"px",WebkitTransform:"rotate("+n+"deg)"})},a=function(){var a=setInterval(t,r.speed),n=function(){clearInterval(a),e.css({WebkitTransform:"rotate(0deg)"})};setTimeout(n,r.duration)};setInterval(a,r.frequency)})};